mapper.select:
select count(id)
from member
where id = #{id} and pw = #{pw}

select count(id)
from member
where id = #{id}

select count(mail)
from member
where mail = #{mail}

select id
from member
where name = #{name} and mail = #{mail}

select count(id)
from member
where id = #{id} and name = #{name} and mail = #{mail}

mapper.update:
update member
set pw = #{pw}
where id = #{id}
